import SketchFormat from '@sketch-hq/sketch-file-format-ts';
import Bitmap from '../Bitmap';

describe('Bitmap 类', () => {
  it('传入 base64 URL 后 可正常生成 Sketch JSON', () => {
    const base64 =
      'iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAYAAAAfFcSJAAAADUlEQVR42mM8w8DwHwAEOQHNmnaaOAAAAABJRU5ErkJggg==';
    const bitmap = new Bitmap({
      url: `data:image/png;base64,${base64}`,
      x: 0,
      y: 0,
      width: 100,
      height: 100,
    });

    expect(
      (bitmap.toSketchJSON().image as SketchFormat.DataRef).data._data,
    ).toEqual(base64);

    expect(bitmap.toSketchJSON()).toMatchSnapshot();
    expect(bitmap.toSketchJSON()).toStrictEqual({
      _class: 'bitmap',
      booleanOperation: -1,
      clippingMaskMode: 0,
      do_objectID: 'UUID',
      clippingMask: '',
      exportOptions: {
        _class: 'exportOptions',
        exportFormats: [],
        includedLayerIds: [],
        layerOptions: 0,
        shouldTrim: false,
      },
      fillReplacesImage: false,
      frame: {
        _class: 'rect',
        constrainProportions: false,
        height: 100,
        width: 100,
        x: 0,
        y: 0,
      },
      hasClippingMask: false,
      image: {
        _class: 'MSJSONOriginalDataReference',
        _ref: 'images/UUID',
        _ref_class: 'MSImageData',
        data: {
          _data:
            'iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAYAAAAfFcSJAAAADUlEQVR42mM8w8DwHwAEOQHNmnaaOAAAAABJRU5ErkJggg==',
        },
        sha1: {
          _data: '',
        },
      },
      intendedDPI: 32,
      isFixedToViewport: false,
      isFlippedHorizontal: false,
      isFlippedVertical: false,
      isLocked: false,
      isVisible: true,
      layerListExpandedType: 0,
      name: 'bitmap',
      nameIsFixed: false,
      resizingConstraint: 63,
      resizingType: 0,
      rotation: 0,
      shouldBreakMaskChain: false,
      style: {
        _class: 'style',
        borderOptions: {
          _class: 'borderOptions',
          dashPattern: [],
          isEnabled: true,
          lineCapStyle: 0,
          lineJoinStyle: 0,
        },
        borders: [],
        colorControls: {
          _class: 'colorControls',
          brightness: 0,
          contrast: 1,
          hue: 0,
          isEnabled: false,
          saturation: 1,
        },
        contextSettings: {
          _class: 'graphicsContextSettings',
          blendMode: 0,
          opacity: 1,
        },
        do_objectID: 'UUID',
        endMarkerType: 0,
        fills: [],
        innerShadows: [],
        miterLimit: 10,
        shadows: [],
        startMarkerType: 0,
        windingRule: 1,
      },
    });
  });
  it('传入 错误 URL 后 无法生成 Sketch JSON', () => {
    const bitmap = new Bitmap({
      url: `sjo.png`,
      x: 0,
      y: 0,
      width: 100,
      height: 100,
    });

    expect(() => bitmap.toSketchJSON()).toThrow(`不正确的图像网址:`);
  });

  describe('ensureBase64DataURL', () => {
    it('base64 则不变', () => {
      const img =
        'data:image/png;base64,';
      const x = Bitmap.ensureBase64DataURL(img);
      expect(x).toBe(img);
    });
    it('不是 base64编译成 base64', () => {
      const sha1Str =
        'data:image/png;sha1,';
      const x = Bitmap.ensureBase64DataURL(sha1Str);

      const base64Str =
        'data:image/png;base64,';
      expect(x).toBe(base64Str);
    });
  });
});
